mapper.select:
SELECT num,

FROM bbs
WHERE num=#{num}

mapper.insert:
      INSERT INTO bbs
      (
      	author
      	, wdate
      	, title
      	, contents
      	, hit
      	, pnum
      ) VALUES(
	      #{author}
	      ,NOW()
	      ,#{title}
	      ,#{contents}
	      ,NULL
	      ,


	      		NULL


	      		#{pnum}


      )


UPDATE bbs
SET title = #{title}, contents = #{contents}, hit=#{hit}
WHERE num=#{num}

mapper.select:
SELECT num,author,wdate,title,contents,hit,pnum
FROM bbs

	 num = #{sVal}

	 title REGEXP #{sVal}

	 author REGEXP #{sVal}

	 contents REGEXP #{sVal}


ORDER BY num DESC
